<script>
function openMenu(){
	if($('#top_menu').css('visibility') == 'hidden'){
		$('#top_menu').css('visibility','visible');
		$('#chat').css('visibility','hidden');
		$('#room_list').css('visibility','hidden');
	}else{
		$('#top_menu').css('visibility','hidden');
		$('#chat').css('visibility','visible');
		$('#room_list').css('visibility','hidden');
	}
}
function openRooms(){
	if($('#room_list').css('visibility') == 'hidden'){
		$('#room_list').css('visibility','visible');
		$('#chat').css('visibility','hidden');
		$('#top_menu').css('visibility','hidden');
	}else{
		$('#room_list').css('visibility','hidden');
		$('#chat').css('visibility','visible');
		$('#top_menu').css('visibility','hidden');
	}
}
</script>
